// src/App.js
import { useState } from "react";
import Login from "./components/Login";
import Vote from "./components/Vote";
import Results from "./components/Results";

function App() {
  const [user, setUser] = useState(null); // stocke l'utilisateur connect√©
  const [refresh, setRefresh] = useState(0); // pour rafra√Æchir les r√©sultats apr√®s vote

  const handleVoteSuccess = () => {
    setRefresh((prev) => prev + 1); // d√©clenche une actualisation des r√©sultats
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>üó≥Ô∏è BayrouMeter</h1>
      {!user ? (
        <Login setUser={setUser} />
      ) : (
        <>
          <p>Bienvenue, {user.pseudo} !</p>
          <Vote user={user} onVoteSuccess={handleVoteSuccess} />
          <Results key={refresh} /> {/* key force le re-render pour refresh */}
        </>
      )}
    </div>
  );
}

export default App;
